mapper.select:
SELECT id, customer_id, shop_id, order_status, address, phone_number, created_at, updated_at
FROM tbl_order
WHERE id = #{orderId}

mapper.update:
UPDATE tbl_order
SET customer_id = #{customerId}, shop_id = #{shopId}, order_status = #{orderStatus}, address = #{address},
phone_number = #{phoneNumber}
WHERE id = #{id};
